import React, {component} from 'react';
import { useFormik } from "formik";

const Addmovie=(props)=>{
    const {pushmovie}=props

const formik=useFormik({
    initialValues:{
        moviename:'',
        genre:'',
        rating:''
    }
});

    return(
        <div className="m-5">
            <form onSubmit={(e)=>{
                e.preventDefault();
                pushmovie(e,formik.values)
            }
            }>
                <div> 
                    <label> Movie Name: </label>
                    <input type="text" className="form-control" name="moviename" id="moviename" placeholder="enter movie name" required autoFocus value={formik.values.moviename} onChange={formik.handleChange}/>
                </div>
                <div> 
                    <label> Genre: </label>
                    <input type="text" className="form-control" name="genre" id="genre" placeholder="enter movie genre" required autoFocus value={formik.values.genre} onChange={formik.handleChange}/>
                </div>
                <div> 
                    <label> how much did you like this movie? (1-10) </label>
                    <input type="number" className="form-control" name="rating" id="rating" placeholder="enter movie rating" required autoFocus value={formik.values.rating} onChange={formik.handleChange}/>
                </div>
                <button type="submit" className="m-3 btn btn-secondary" onClick={(e)=>onsubmit(e,formik.values)}>
                    Send Movie
                </button>
            </form>
        </div>
    )
}
export default Addmovie;
