import React, { Component } from 'react';

class Filtercom extends Component {
    state={
        people:[
            "Shashi Koshy",
            "Dhriti Taneja",
            "Dipa Mishra",
            "Ansh Thakkar",
            "Lakshmi Thaker",
            "Sushila Matthai",
            "Shresth Nigam",
            "Bhavana Biswas",
            "Vasudha Mangat",
            "Priya Saran"
           ]
    }
    render() {
        return (
            <div>
                <div className='ms-5 mt-5'>
                    <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" onChange={()=> this.Rfil()}/>
                        <label className="form-check-label" for="flexCheckDefault">includes R or r</label>
                    </div>
                    <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" onChange={()=> this.Lfil()}/>
                        <label className="form-check-label" for="flexCheckDefault">length {'>'} 14</label>
                    </div>
                    <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" onChange={()=> this.Bfil()}/>
                        <label className="form-check-label" for="flexCheckDefault">start with "bh"</label>
                    </div>
                </div>
                <div className='m-2'>
                    <ul>
                        {this.state.people.map(name=><li>{name}</li>)}
                    </ul>
                </div>
            </div>
        );
    }
    Rfil(){
        chacked? this.state.people.filter(name=>name.includes('r'||'R')): people;
        this.setState(people);
        return people
    }
    Lfil(){
        let people=this.state.people.filter(name=>name.length>14);
        this.setState(people);
        return(people)
    }
    Bfil(){
        let people=this.state.people.filter(name=>name.startsWith('Bh'));
        this.setState(people);
        return(people)
    }
}

export default Filtercom;
