import React, { Component } from 'react';
import Movie from './movie';

class Movies extends Component {
    state = {  
        movies:[
            {
                id:0,
                name: 'die hard',
                genre: 'Action',
                length: '2:20'
            },
            {
                id:1,
                name: 'die hard2',
                genre: 'Action',
                length: '2:30'
            },
            {
                id:2,
                name: 'fast five',
                genre: 'Action',
                length: '4:20'
            },
            {
                id:3,
                name: 'Taken',
                genre: 'Action',
                length: '1:20'
            }
        ]
     } 
    render() { 
        return (
            <div>
                <table className="table">
                    <thead>
                    <tr>
                        <th>movie's name</th>
                        <th>id</th>
                        <th>ganre</th>
                        <th>length</th>
                        <th>delete</th>
                    </tr>
                    </thead>
                    <tbody>
                    {this.state.movies.map(movie=>(
                    <tr>
                        <td>{movie.name}</td>
                        <td>{movie.id}</td>
                        <td>{movie.genre}</td>
                        <td>{movie.length}</td>
                        <td><button className='btn btn-outline-danger' onClick={()=>this.state.movies=this.setState({movies:delmovie(movie.id)})}>delete</button></td>
                    </tr>
                    ))}</tbody>
                </table>
            </div>
        );
    }
}

export default Movies;
