import React, { Component } from 'react';
import Movie from './movie';

class Movies extends Component {
    state = {  
        movies:[
            {
                id:0,
                name: 'die hard',
                genre: 'Action',
                length: '2:20'
            },
            {
                id:1,
                name: 'die hard2',
                genre: 'Action',
                length: '2:30'
            },
            {
                id:2,
                name: 'fast five',
                genre: 'Action',
                length: '4:20'
            },
            {
                id:3,
                name: 'Taken',
                genre: 'Action',
                length: '1:20'
            }
        ]
     } 
    render() { 
        return (
            <React.Fragment>
                {this.state.movies.map( m => {
                    return <Movie key={m.id} id={m.id} name={m.name} genre={m.genre} length={m.length}/>
                } )}
            </React.Fragment>
        )
    }
}

export default Movies;
