import React,{Component} from 'react';
class Counter extends Component {
    state={
        count:x,
    };
    render() { 
        return <div>
            <p>{this.formatcount()}</p>
            <input type="text" id='x'/>
            <button>+</button>
            <button>-</button>
        </div>;
    }
    formatcount(x){
        return document.getElementById('x')===0?document.getElementById('x').innerHTML='zero':x;
    }
};

export default Counter;
