import React, { Component } from 'react';

class Filtercom extends Component {
    state={
        people:[
            "Shashi Koshy",
            "Dhriti Taneja",
            "Dipa Mishra",
            "Ansh Thakkar",
            "Lakshmi Thaker",
            "Sushila Matthai",
            "Shresth Nigam",
            "Bhavana Biswas",
            "Vasudha Mangat",
            "Priya Saran"
           ],
           r:false,
           l:false,
           b:false
    }
    render() {
        return (
            <div>
                <div className='ms-5 mt-5'>
                    <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" defaultChecked={this.state.r} onChange={()=> this.Rfil()}/>
                        <label className="form-check-label" for="flexCheckDefault">includes R or r</label>
                    </div>
                    <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" defaultChecked={this.state.l} onChange={()=> this.state.people.map()}/>
                        <label className="form-check-label" for="flexCheckDefault">length {'>'} 14</label>
                    </div>
                    <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" defaultChecked={this.state.b} onChange={()=> this.state.people.map()}/>
                        <label className="form-check-label" for="flexCheckDefault">start with "bh"</label>
                    </div>
                </div>
            </div>
        );
    }
    Rfil(){
        let people=this.state.people.filter(name=>name.includes('r'||'R'));
        this.setState(people);
        console.log(people)
    }
    Lfil(){
        let people=this.state.people.filter(name=>name.length>14);
        this.setState(people);
        console.log(people)
    }
    Bfil(){
        let people=this.state.people.filter(name=>name.includes('r'||'R'));
        this.setState(people);
        console.log(people)
    }
}

export default Filtercom;
