import React,{Component} from 'react';
class Counter extends Component {
    state={
        count:0,
    };
    render() { 
        return <div>
            <p style="border: 2px; solid; black">{this.formatcount()}</p>
            <input type="text"/>
            <button>+</button>
            <button>-</button>
        </div>;
    }
    formatcount(){
        return this.state.count===0?'zero':this.state.count;
    }
};

export default Counter;
